function changePic(){num=(num+1)%5,document.getElementById("inner-img").src="images/"+String(num)+".jpg";var e=document.getElementById("number"+String(num));e.setAttribute("class","numbers-marked");var t=document.getElementById("number"+String((num+4)%5));t.setAttribute("class","numbers")}function clickChangePic(){for(var e=num,t=0;t<5;t++){var n=document.getElementById("number"+String(t));n.setAttribute("class","numbers")}document.getElementById("inner-img").src="images/pic"+String(e)+".jpg";var r=document.getElementById("number"+String(e));r.setAttribute("class","numbers-marked");var m=document.getElementById("number"+String((e+4)%5));m.setAttribute("class","numbers"),window.setInterval("clickChangePic()",3e3)}function pageNumber(e){var t=parseInt(e),n=document.getElementById("number"+String(t-1));n.setAttribute("class","numbers-marked");for(var r=0;r<5;r++)if(r!=t-1){var m=document.getElementById("number"+String(r));m.setAttribute("class","numbers")}var u=document.getElementById("inner-img");u.src="./images/"+String(t-1)+".jpg",num=t-1}var num=0;window.setInterval("changePic()",3e3);